import React, { useState, useEffect } from "react";
import API from "../utils/API";
import { Link } from "react-router-dom";
import { Col, Row, Container } from "../components/Grid";
import { Input, TextArea, FormBtn } from "../components/Form";

function Users() {
    const [formObject, setFormObject] = useState({})

    useEffect(() => {
        getUsers()
    }, [])

    function getUsers() {
        API.getUsers()
            .then(res =>
                setUsers(res.data)
            )
            .catch(err => console.log(err));
    };
    function handleInputChange(event) {
        const { name, value } = event.target;
        setFormObject({ ...formObject, [name]: value })
    };

    function handleFormSubmit(event) {
        event.preventDefault();
        if (formObject.first_name && formObject.last_name && formObject.username) {
            API.saveUser({
                first_name: formObject.first_name,
                last_name: formObject.last_name,
                email: formObject.email,
                username: formObject.username,
                password: formObject.password
            })
                .then(res => loadUsers())
                .catch(err => console.log(err));
        }
    };

    return (
        <Container fluid>
            <Row>
                <Col>
                    <h1>Blaises Contact Form</h1>
                </Col>
            </Row>
            <Row>
                <form>
                    <Input
                        onChange={handleInputChange}
                        name="first_name"
                        placeholder="First Name (required)"
                    />
                    <Input
                        onChange={handleInputChange}
                        name="last_name"
                        placeholder="Last Name (required)"
                    />
                    <Input
                        onChange={handleInputChange}
                        name="email"
                        placeholder="Email (required)"
                    />
                    <Input
                        onChange={handleInputChange}
                        name="username"
                        placeholder="Username (required)"
                    />
                    <Input
                        onChange={handleInputChange}
                        name="password"
                        placeholder="Password (required)"
                    />
                    <Input
                        onChange={handleInputChange}
                        name="password"
                        placeholder="Confirm Password (required)"
                    />
                    <FormBtn
                        disabled={!(formObject.first_name && formObject.last_name && formObject.username)}
                        onClick={handleFormSubmit}
                    >
                        Submit New User
              </FormBtn>
                </form>
            </Row>
        </Container>
    )
};

module.exports = Users
